/**
 * Solves the weighted interval scheduling problem to find the maximum value
 * of non-overlapping intervals where each interval's value is 100 * (end - start + 1).
 */
function weightedIntervalScheduling(intervals: [number, number][]): number {
    // Sort intervals by end time, and by start time if ends are equal
    intervals.sort((a, b) => a[1] - b[1] || a[0] - b[0]);

    const endTimes = intervals.map(interval => interval[1]);
    const dp: number[] = new Array(intervals.length + 1).fill(0);

    for (let i = 1; i <= intervals.length; i++) {
        const [start, end] = intervals[i - 1];
        const value = 100 * (end - start + 1);

        // Find the rightmost interval that ends before current interval starts
        let k = binarySearchRight(endTimes, start - 1, 0, i - 1);

        const option1 = dp[k] + value;
        const option2 = dp[i - 1];
        dp[i] = Math.max(option1, option2);
    }

    return dp[intervals.length];
}

/**
 * Binary search implementation similar to Python's bisect.bisect_right
 * Returns the index where the value should be inserted to maintain order
 */
function binarySearchRight(
    arr: number[],
    target: number,
    left: number = 0,
    right: number = arr.length
): number {
    while (left < right) {
        const mid = Math.floor((left + right) / 2);
        if (arr[mid] <= target) {
            left = mid + 1;
        } else {
            right = mid;
        }
    }
    return left;
}

