/**
 * Solves the problem of maximizing time allocation with options to drink water.
 * The function calculates the maximum time achievable by either:
 * 1. Not drinking water at all (using times A and B directly).
 * 2. Drinking water at some point, which halves the current time and then continues with times A and B.
 * 
 * @param T - Total available time.
 * @param A - Time option A.
 * @param B - Time option B.
 * @returns The maximum achievable time.
 */
function maximizeTime(T: number, A: number, B: number): number {
    // Initialize dp1: tracks achievable times without drinking water
    const dp1: boolean[] = new Array(T + 1).fill(false);
    dp1[0] = true;
    let maxWithout = 0;

    for (let i = 1; i <= T; i++) {
        if ((i >= A && dp1[i - A]) || (i >= B && dp1[i - B])) {
            dp1[i] = true;
            if (i > maxWithout) {
                maxWithout = i;
            }
        }
    }

    // Initialize dp2: tracks achievable times after drinking water
    const dp2: boolean[] = new Array(T + 1).fill(false);
    let maxWith = 0;

    // For each achievable time in dp1, simulate drinking water (halving the time)
    for (let i = 0; i <= T; i++) {
        if (dp1[i]) {
            const j = Math.floor(i / 2);
            if (j <= T && !dp2[j]) {
                dp2[j] = true;
                // Perform knapsack from j onwards
                for (let k = j; k <= T; k++) {
                    if ((k >= A && dp2[k - A]) || (k >= B && dp2[k - B])) {
                        if (!dp2[k]) {
                            dp2[k] = true;
                            if (k > maxWith) {
                                maxWith = k;
                            }
                        }
                    }
                }
            }
        }
    }

    // The answer is the maximum between maxWithout and maxWith
    return Math.max(maxWithout, maxWith);
}

