/**
 * Finds the minimum length of a substring in `strInput` that contains all characters of `S`.
 * @param S - The string containing characters to be included in the substring.
 * @param strInput - The input string to search within.
 * @returns The minimum length of the substring containing all characters of `S`. Returns 0 if no such substring exists.
 */
function findMinSecretSubstring(S: string, strInput: string): number {
    const need: Record<string, number> = {};
    for (const c of S) {
        need[c] = 1; // Each required character needs to appear once
    }

    const window: Record<string, number> = {};
    let left = 0;
    let valid = 0;
    let minLen = Infinity;

    for (let right = 0; right < strInput.length; right++) {
        const c = strInput[right];
        if (need[c]) {
            window[c] = (window[c] || 0) + 1;
            if (window[c] === need[c]) {
                valid++;
            }
        }

        // When the window meets the condition, try to shrink the left pointer
        while (valid === Object.keys(need).length) {
            const currentLen = right - left + 1;
            if (currentLen < minLen) {
                minLen = currentLen;
            }
            const d = strInput[left];
            if (need[d]) {
                if (window[d] === need[d]) {
                    valid--;
                }
                window[d]--;
            }
            left++;
        }
    }

    return minLen === Infinity ? 0 : minLen;
}
