/**
 * Returns the total number of good buildings in the given sequence of heights.
 * A building is considered good if all buildings to its left are smaller in height
 * and all buildings to its right are larger in height.
 * 
 * @param heights - Array of numbers representing building heights
 * @returns The count of good buildings
 */
function countGoodBuildings(heights: number[]): number {
    const n = heights.length;
    if (n === 0) {
        return 0;
    }

    let goodBuildingsCount = 0;

    for (let i = 0; i < n; i++) {
        let isGood = true;

        // Check all buildings to the left
        for (let j = 0; j < i; j++) {
            if (heights[j] >= heights[i]) {
                isGood = false;
                break;
            }
        }

        // If left check passed, check all buildings to the right
        if (isGood) {
            for (let k = i + 1; k < n; k++) {
                if (heights[k] <= heights[i]) {
                    isGood = false;
                    break;
                }
            }
        }

        if (isGood) {
            goodBuildingsCount++;
        }
    }

    return goodBuildingsCount;
}

