/**
 * Determines if the triangle with sides a, b, and c is a right-angled triangle.
 * 
 * @param a - Side a of the triangle
 * @param b - Side b of the triangle
 * @param c - Side c of the triangle
 * @returns "YES" if the triangle is right-angled, "NO" otherwise
 */
function isRightAngleTriangle(a: number, b: number, c: number): string {
    const sides = [a, b, c].sort((x, y) => x - y);
    if (Math.pow(sides[0], 2) + Math.pow(sides[1], 2) === Math.pow(sides[2], 2)) {
        return "YES";
    } else {
        return "NO";
    }
}

