/**
 * Returns the sum of all prime numbers less than N using the Sieve of Eratosthenes.
 * If N is less than 2, returns 0 since there are no primes less than 2.
 */
function sumOfPrimes(N: number): number {
    if (N < 2) {
        return 0;
    }

    // Initialize a boolean array that will store whether each number is prime
    const isPrime: boolean[] = new Array(N).fill(true);
    isPrime[0] = isPrime[1] = false; // 0 and 1 are not prime numbers

    // Implementing Sieve of Eratosthenes
    let p = 2;
    while (p * p < N) {
        if (isPrime[p]) {
            for (let i = p * p; i < N; i += p) {
                isPrime[i] = false;
            }
        }
        p += 1;
    }

    // Calculate the sum of all prime numbers less than N
    let primeSum = 0;
    for (let i = 0; i < isPrime.length; i++) {
        if (isPrime[i]) {
            primeSum += i;
        }
    }

    return primeSum;
}

