    assert.strictEqual(sumOfPrimes(1), 0);
    assert.strictEqual(sumOfPrimes(2), 0);
    assert.strictEqual(sumOfPrimes(10), 17);
    assert.strictEqual(sumOfPrimes(20), 77);
    assert.strictEqual(sumOfPrimes(100), 1060);
}

testSumOfPrimes();

