/**
 * Checks if a directed graph represented by an adjacency list contains a cycle.
 * @param dependencies - The adjacency list representing the graph, where keys are vertices and values are arrays of neighboring vertices.
 * @returns True if the graph contains a cycle, otherwise False.
 */
function hasCycle(dependencies: Record<number, number[]>): boolean {
    // Helper function for DFS traversal
    const dfs = (v: number): void => {
        if (visited[v] === 1) { // visiting (gray)
            hasCycleFlag = true;
            return;
        }
        if (visited[v] === 2) { // visited (black)
            return;
        }

        visited[v] = 1; // mark as visiting (gray)
        for (const neighbor of dependencies[v] || []) {
            dfs(neighbor);
        }
        visited[v] = 2; // mark as visited (black)
    };

    // Initialize visited status for all vertices: 0 = unvisited, 1 = visiting, 2 = visited
    const visited: Record<number, number> = {};
    for (const vertex in dependencies) {
        visited[parseInt(vertex)] = 0;
    }

    let hasCycleFlag = false;

    for (const vertex in dependencies) {
        const vertexNum = parseInt(vertex);
        if (visited[vertexNum] === 0) { // If the vertex is unvisited, perform DFS from it
            dfs(vertexNum);
        }
        if (hasCycleFlag) {
            return true;
        }
    }

    return false;
}

