/**
 * Finds all occurrences of a pattern in a string and returns the start and end indices of each occurrence.
 * @param s The string to search within.
 * @param pattern The pattern to search for.
 * @returns An array of tuples, where each tuple contains the start and end indices of an occurrence.
 */
function findOccurrences(s: string, pattern: string): [number, number][] {
    const lenP = pattern.length;
    const occurrences: [number, number][] = [];
    for (let i = 0; i <= s.length - lenP; i++) {
        if (s.substring(i, i + lenP) === pattern) {
            const start = i;
            const end = i + lenP - 1;
            occurrences.push([start, end]);
        }
    }
    return occurrences;
}

/**
 * Processes test cases to find the smallest substring containing both patterns.
 * The function prints "No" if either pattern does not occur in the string.
 * Otherwise, it prints the smallest substring (by length and lexicographical order) that contains both patterns.
 */
function findSmallestSubstring(s1: string, s2: string, s3: string): string | "No" {
    const A = findOccurrences(s1, s2);
    const B = findOccurrences(s1, s3);

    if (A.length === 0 || B.length === 0) {
        return "No";
    }

    const candidates: string[] = [];
    for (const a of A) {
        const [aStart, aEnd] = a;
        for (const b of B) {
            const [bStart, bEnd] = b;
            const start = Math.min(aStart, bStart);
            const end = Math.max(aEnd, bEnd);
            const substr = s1.substring(start, end + 1);
            candidates.push(substr);
        }
    }

    candidates.sort((x, y) => {
        if (x.length !== y.length) {
            return x.length - y.length;
        }
        return x.localeCompare(y);
    });

    return candidates[0];
}

