/**
 * Returns the minimum number of operations to make all elements of the list equal.
 * Each operation is defined as incrementing or decrementing an element by 1.
 * The optimal target value is the median of the sorted array.
 * @param nums - An array of numbers to be equalized.
 * @returns The minimum number of operations required.
 */
function minOperationsToEqualize(nums: number[]): number {
    if (nums.length === 0) {
        return 0;
    }

    // Sort the array to find the median
    const sortedNums = [...nums].sort((a, b) => a - b);

    // Find the median (middle element for odd length, any of the two middle elements for even length)
    const median = sortedNums[Math.floor(sortedNums.length / 2)];

    // Calculate the total operations required to make all elements equal to the median
    const operations = sortedNums.reduce((sum, num) => sum + Math.abs(num - median), 0);

    return operations;
}

