/**
 * Tracks the current status of each book based on transactions.
 * 
 * @param transactions - A list of transactions, each represented as an array of ['B'|'R', book_id, transaction_time].
 * @returns A dictionary with book ID as keys and its status ('Borrowed'|'Available') as values.
 */
function trackBooks(transactions: Array<['B' | 'R', string, number]>): Record<string, string> {
    const bookStatus: Record<string, string> = {};

    for (const transaction of transactions) {
        const [transactionType, bookId, _] = transaction;

        if (transactionType === 'B') {
            bookStatus[bookId] = 'Borrowed';
        } else if (transactionType === 'R') {
            bookStatus[bookId] = 'Available';
        }
    }

    return bookStatus;
}

