/**
 * Determines the winner of the game based on the sum of numbers.
 * If the sum is even, Marta wins; otherwise, Niko wins.
 * @param numbers Array of numbers to sum
 * @returns The winner's name as a string
 */
function determineWinner(numbers: number[]): string {
    const finalSum = numbers.reduce((acc, num) => acc + num, 0);
    return finalSum % 2 === 0 ? "Marta" : "Niko";
}

/**
