function martaAndNikoGame(t: number, testCases: [number, number[]][]): string[] {
    const results: string[] = [];
    for (let i = 0; i < t; i++) {
        const [n, numbers] = testCases[i];
        results.push(determineWinner(numbers));
    }
    return results;
}

