/**
 * Checks if two rectangles overlap.
 * Rectangles are represented as [x1, y1, x2, y2], where (x1,y1) is the bottom-left corner and (x2,y2) is the top-right corner.
 */
function doOverlap(r1: number[], r2: number[]): boolean {
    // Check if one rectangle is to the left of the other
    if (r1[2] <= r2[0] || r1[0] >= r2[2]) {
        return false;
    }
    // Check if one rectangle is above/below the other
    if (r1[3] <= r2[1] || r1[1] >= r2[3]) {
        return false;
    }
    return true;
}

/**
 * Counts the number of intersecting pairs of rectangles for each test case.
 * @param T Number of test cases
 * @param testCases Array of test cases, where each test case is [N, rectangles]
 * @returns Array of counts of intersecting pairs for each test case
 */
function intersectingPairs(T: number, testCases: [number, number[][]][]): number[] {
    const results: number[] = [];
    for (let i = 0; i < T; i++) {
        const [N, rectangles] = testCases[i];
        let count = 0;
        for (let j = 0; j < N; j++) {
            for (let k = j + 1; k < N; k++) {
                if (doOverlap(rectangles[j], rectangles[k])) {
                    count++;
                }
            }
        }
        results.push(count);
    }
    return results;
}

