/**
 * Finds the two distinct elements in the list with the smallest absolute difference.
 * Returns a tuple of the two closest numbers.
 * If the list has fewer than 2 elements, returns [0, 0].
 */
function closestPair(numbers: number[]): [number, number] {
    if (numbers.length < 2) {
        return [0, 0];
    }

    // Sort the array to make it easier to find closest pairs
    numbers.sort((a, b) => a - b);

    // Initialize the smallest difference to a large number
    let smallestDiff = Infinity;
    let closestPair: [number, number] = [0, 0];

    // Iterate through sorted array and check consecutive pairs
    for (let i = 0; i < numbers.length - 1; i++) {
        const diff = Math.abs(numbers[i] - numbers[i + 1]);
        if (diff < smallestDiff) {
            smallestDiff = diff;
            closestPair = [numbers[i], numbers[i + 1]];
        }
    }

    return closestPair;
}

