/**
 * Simulates a series of card shuffling operations on an array of numbers.
 * The operations include riffle shuffle (k=0) and cut (k>0).
 * 
 * @param n - Half the length of the initial array (initial array will be [1, 2, ..., 2n])
 * @param ops - Array of operations where 0 indicates riffle shuffle and positive numbers indicate cut positions
 * @returns The final array after performing all operations
 */
function simulateShuffles(n: number, ops: number[]): number[] {
    // Initialize the array with 1 to 2n
    let a: number[] = Array.from({ length: 2 * n }, (_, i) => i + 1);

    for (const k of ops) {
        if (k === 0) {
            // Perform riffle shuffle
            const newA: number[] = [];
            for (let i = 0; i < n; i++) {
                newA.push(a[i]);
                newA.push(a[n + i]);
            }
            a = newA;
        } else {
            // Perform cut
            a = [...a.slice(k), ...a.slice(0, k)];
        }
    }

    return a;
}

