    assert.deepStrictEqual(simulateShuffles(4, [0, 0]), [1, 3, 5, 7, 2, 4, 6, 8]);
    assert.deepStrictEqual(simulateShuffles(3, [2]), [3, 4, 5, 6, 1, 2]);
    assert.deepStrictEqual(simulateShuffles(1, [0]), [1, 2]);
}

