// Define a TreeNode class to represent nodes in a binary search tree
class TreeNode {
    val: number;
    left: TreeNode | null;
    right: TreeNode | null;

    constructor(val: number) {
        this.val = val;
        this.left = null;
        this.right = null;
    }
}

/**
 * Inserts a value into a binary search tree rooted at the given node.
 * If the node is null, creates a new node with the given value.
 * Returns the root of the modified tree.
 */
function insert(root: TreeNode | null, val: number): TreeNode {
    if (root === null) {
        return new TreeNode(val);
    }
    if (val < root.val) {
        root.left = insert(root.left, val);
    } else {
        root.right = insert(root.right, val);
    }
    return root;
}

/**
 * Generates a structural representation of a binary tree.
 * The structure is represented as a nested array where each node is represented as [left, right].
 * Returns null for a null node.
 */
function generateStructure(node: TreeNode | null): [any, any] | null {
    if (node === null) {
        return null;
    }
    const left = generateStructure(node.left);
    const right = generateStructure(node.right);
    return [left, right];
}

/**
 * Computes the number of unique binary search tree structures from a list of sequences.
 * Each sequence is inserted into a BST, and the structure of each BST is recorded.
 * Returns the count of unique structures.
 */
function countUniqueStructures(sequences: number[][]): number {
    const uniqueStructures = new Set<string>();

    for (const sequence of sequences) {
        let root: TreeNode | null = null;
        for (const num of sequence) {
            root = insert(root, num);
        }
        const structure = generateStructure(root);
        // Convert the structure to a JSON string for easy comparison in the Set
        uniqueStructures.add(JSON.stringify(structure));
    }

    return uniqueStructures.size;
}

assertions:
const assert = require('assert');

function testCountUniqueStructures() {
    // Test Case 1: All sequences produce the same structure
    assert.strictEqual(countUniqueStructures([
        [3, 1, 2],
        [2, 1, 3],
        [1, 2, 3]
    ]), 1);

    // Test Case 2: First and third sequences produce the same structure, second is different
    assert.strictEqual(countUniqueStructures([
        [5, 3, 4, 2, 1],
        [1, 2, 3, 4, 5],
        [4, 2, 1, 3, 5]
    ]), 2);

    // Test Case 3: All sequences produce the same single-node structure
    assert.strictEqual(countUniqueStructures([
        [1],
        [2],
        [3]
    ]), 1);
}

testCountUniqueStructures();

