/**
 * Processes a series of operations on a sorted array with cumulative additions and calculates the total sum based on a target value.
 */

/**
 * Calculates the prefix sum of a sorted array.
 * @param arr The sorted array of numbers.
 * @returns The prefix sum array where prefixSum[i] is the sum of the first i elements.
 */
function calculatePrefixSum(arr: number[]): number[] {
    const prefixSum = [0];
    for (let i = 0; i < arr.length; i++) {
        prefixSum.push(prefixSum[i] + arr[i]);
    }
    return prefixSum;
}

/**
 * Processes the operations and calculates the required totals.
 * @param n The number of elements in the array.
 * @param m The number of operations.
 * @param a The initial array of numbers.
 * @param operations The list of operations to perform.
 * @returns An array of results for each '2' operation.
 */
function processOperations(n: number, m: number, a: number[], operations: string[]): number[] {
    const sortedA = [...a].sort((x, y) => x - y);
    const prefixSum = calculatePrefixSum(sortedA);
    let totalAdd = 0;
    const results: number[] = [];

    for (const op of operations) {
        const parts = op.split(' ');
        if (parts[0] === '1') {
            const k = parseInt(parts[1], 10);
            totalAdd += k;
        } else {
            const K = totalAdd;
            const target = -K;
            // Find the first index where sortedA[index] > target
            let pos = 0;
            while (pos < sortedA.length && sortedA[pos] <= target) {
                pos++;
            }
            const total = (prefixSum[n] - prefixSum[pos]) + K * (n - pos);
            results.push(Math.max(total, 0));
        }
    }

    return results;
}
const assert = require('assert');

function testProcessOperations() {
    // Test Case 1
    assert.deepStrictEqual(
        processOperations(5, 3, [1, 2, 3, 4, 5], ["2", "1 2", "2"]),
