/**
 * Adds the minimum number of elements to an array such that there are exactly k different integers
 * in the final array. If it is not possible, returns -1.
 * 
 * @param arr The input array of numbers.
 * @param k The desired number of unique elements in the array.
 * @returns The minimum number of elements to add, or -1 if it's not possible.
 */
function addMinimumElements(arr: number[], k: number): number {
    const uniqueElements = new Set(arr);
    const numUniqueElements = uniqueElements.size;

    if (numUniqueElements === k) {
        return 0;
    } else if (numUniqueElements > k) {
        return -1;
    } else {
        return k - numUniqueElements;
    }
}

