/**
 * Calculates the number of submatrices in a given matrix that sum to a target value.
 * 
 * @param matrix - A 2D array of numbers representing the matrix.
 * @param target - The target sum to find.
 * @returns The number of submatrices that sum to the target value.
 */
function numSubmatrixSumTarget(matrix: number[][], target: number): number {
    const m = matrix.length;
    const n = matrix[0].length;

    // To store the cumulative sum of elements in each row, column-wise.
    const cumulativeRowSum: number[][] = Array.from({ length: m }, () => new Array(n + 1).fill(0));

    for (let i = 0; i < m; i++) {
        for (let j = 0; j < n; j++) {
            cumulativeRowSum[i][j + 1] = cumulativeRowSum[i][j] + matrix[i][j];
        }
    }

    let result = 0;

    for (let colStart = 0; colStart < n; colStart++) {
        for (let colEnd = colStart; colEnd < n; colEnd++) {
            const sumFreq: Record<number, number> = { 0: 1 };
            let currSum = 0;

            for (let row = 0; row < m; row++) {
                currSum += cumulativeRowSum[row][colEnd + 1] - cumulativeRowSum[row][colStart];
                result += sumFreq[currSum - target] || 0;
                sumFreq[currSum] = (sumFreq[currSum] || 0) + 1;
            }
        }
    }

    return result;
}

