/**
 * Returns the number of pairs of sequential checkpoints with a difference of exactly K.
 * 
 * @param N Number of checkpoints
 * @param K Desired difference between checkpoints
 * @param distances List of distances at each checkpoint in non-decreasing order
 * @returns Number of pairs with the desired difference
 */
function countPairsWithDifferenceK(N: number, K: number, distances: number[]): number {
    let count = 0;
    for (let i = 1; i < N; i++) {
        if (distances[i] - distances[i - 1] === K) {
            count++;
        }
    }
    return count;
}

