/**
 * Determines the minimum number of characters to add to make the password strong.
 * A strong password must:
 * - Be at least 6 characters long
 * - Contain at least one digit
 * - Contain at least one lowercase letter
 * - Contain at least one uppercase letter
 * - Contain at least one special character from "!@#$%^&*()-+"
 * @param n The length of the current password
 * @param password The password string to check
 * @returns The minimum number of characters to add to make the password strong
 */
function strongPassword(n: number, password: string): number {
    // Initialize the required types of characters with false
    let hasDigit = false;
    let hasLower = false;
    let hasUpper = false;
    let hasSpecial = false;

    const specialCharacters = "!@#$%^&*()-+";

    // Check already existing characters type in password
    for (const char of password) {
        if (char >= '0' && char <= '9') {
            hasDigit = true;
        } else if (char >= 'a' && char <= 'z') {
            hasLower = true;
        } else if (char >= 'A' && char <= 'Z') {
            hasUpper = true;
        } else if (specialCharacters.includes(char)) {
            hasSpecial = true;
        }
    }

    // Count how many types are missing
    const missingTypes = 
        (hasDigit ? 0 : 1) + 
        (hasLower ? 0 : 1) + 
        (hasUpper ? 0 : 1) + 
        (hasSpecial ? 0 : 1);

    return Math.max(6 - n, missingTypes);
}

