/**
 * Creates mapping dictionaries for lowercase and uppercase character substitutions
 * @param sourceChars - The source character set
 * @param targetChars - The target character set
 * @returns Two maps: one for lowercase and one for uppercase substitutions
 */
function createSubstitutionMaps(sourceChars: string, targetChars: string): [Record<string, string>, Record<string, string>] {
    const lowerMap: Record<string, string> = {};
    const upperMap: Record<string, string> = {};

    for (let i = 0; i < 26; i++) {
        const sChar = sourceChars[i];
        const wChar = targetChars[i];
        lowerMap[sChar] = wChar;
        upperMap[sChar.toUpperCase()] = wChar.toUpperCase();
    }

    return [lowerMap, upperMap];
}

/**
 * Applies character substitution to input text using the provided maps
 * @param text - The text to process
 * @param lowerMap - Map for lowercase substitutions
 * @param upperMap - Map for uppercase substitutions
 * @returns The processed text with substitutions applied
 */
function substituteCharacters(text: string, lowerMap: Record<string, string>, upperMap: Record<string, string>): string {
    const result: string[] = [];

    for (const char of text) {
        if (char in lowerMap) {
            result.push(lowerMap[char]);
        } else if (char in upperMap) {
            result.push(upperMap[char]);
        } else {
            result.push(char);
        }
    }

    return result.join('');
}

