/**
 * Modifies the input array in place such that all elements divisible by K appear at the start
 * and the remaining elements follow, all while maintaining the relative order of the elements.
 * @param arr The input array to be sorted.
 * @param N The length of the array (unused in this implementation, but kept for consistency with the original function signature).
 * @param K The divisor to check for element divisibility.
 */
function specialSort(arr: number[], N: number, K: number): void {
    const divisible = arr.filter(x => x % K === 0);
    const nonDivisible = arr.filter(x => x % K !== 0);

    // Combine both lists and update the original array in place
    arr.length = 0; // Clear the original array
    arr.push(...divisible, ...nonDivisible);
}

