 * @param k - The number of elements to choose.
 * @returns The binomial coefficient C(n, k).
 * @throws {Error} If k > n, since it's not possible to choose more elements than available.
 */
function iterativeBinomialCoefficient(n: number, k: number): number {
    // Handle base case
    if (k > n) {
        throw new Error('Invalid Inputs, ensure that n >= k');
    }

    // Create a 2D array to store values of the binomial coefficients
    const C: number[][] = Array.from({ length: n + 1 }, () => Array(k + 1).fill(0));

    // Calculate value of Binomial Coefficient in bottom-up manner
    for (let i = 0; i <= n; i++) {
        for (let j = 0; j <= Math.min(i, k); j++) {
            // Base cases
            if (j === 0 || j === i) {
                C[i][j] = 1;
            }
            // Calculate value using previously stored values
            else {
                C[i][j] = C[i - 1][j - 1] + C[i - 1][j];
            }
        }
    }

    return C[n][k];
}

