/**
 * Processes a string by collapsing adjacent 'o's into 'O's and removing adjacent 'O's.
 * The function uses a stack to process the string character by character.
 * @param s The input string to process.
 * @returns The processed string after applying the bubble rules.
 */
function processBubbles(s: string): string {
    const stack: string[] = [];
    for (const c of s) {
        stack.push(c);
        while (true) {
            if (stack.length >= 2) {
                const a = stack[stack.length - 2];
                const b = stack[stack.length - 1];
                if (a === 'o' && b === 'o') {
                    stack.pop();
                    stack.pop();
                    stack.push('O');
                } else if (a === 'O' && b === 'O') {
                    stack.pop();
                    stack.pop();
                } else {
                    break;
                }
            } else {
                break;
            }
        }
    }
    return stack.join('');
}

