/**
 * Calculates the total score of cards based on the given algorithm.
 * The algorithm sorts the cards and sums the minimum of each consecutive sequence.
 * Consecutive numbers (differing by 1) are considered part of the same sequence.
 * 
 * @param cards - Array of card values
 * @returns Total calculated score
 */
function calculateCardScore(cards: number[]): number {
    if (cards.length === 0) {
        return 0;
    }

    // Sort the cards in ascending order
    const sortedCards = [...cards].sort((a, b) => a - b);

    let totalScore = 0;
    let currentMin = sortedCards[0];

    for (let i = 1; i < sortedCards.length; i++) {
        if (sortedCards[i] === sortedCards[i - 1] + 1) {
            // Continue the current sequence
            continue;
        } else {
            // End of sequence, add currentMin to total and start new sequence
            totalScore += currentMin;
            currentMin = sortedCards[i];
        }
    }

    // Add the last sequence's minimum
    totalScore += currentMin;

    return totalScore;
}

