/**
 * Calculates the minimum number of elements to remove to make the remaining array
 * a sequence of consecutive increasing numbers.
 * 
 * This works by finding the longest sequence of consecutive numbers where each
 * subsequent number appears at a higher index than the previous one.
 * 
 * @param arr - The input array of numbers
 * @returns The minimum number of elements to remove (n - length of longest consecutive increasing sequence)
 */
function minRemovalsForConsecutiveSequence(arr: number[]): number {
    // Create a map from number to its original index
    const pos = new Map<number, number>();
    arr.forEach((num, index) => pos.set(num, index));

    // Sort the array to find consecutive sequences
    const sortedArr = [...arr].sort((a, b) => a - b);

    const dp = new Map<number, number>();
    let maxLen = 0;

    for (const num of sortedArr) {
        const prev = num - 1;

        if (pos.has(prev)) {
            // If previous number appears before current number in original array
            if (pos.get(prev)! < pos.get(num)!) {
                dp.set(num, (dp.get(prev) || 0) + 1);
            } else {
                dp.set(num, 1);
            }
        } else {
            dp.set(num, 1);
        }

        // Update maximum length found
        if (dp.get(num)! > maxLen) {
            maxLen = dp.get(num)!;
        }
    }

    return arr.length - maxLen;
}

