/**
 * Processes an array of test cases for string transformation.
 * For strings with length <= 3, returns the string as-is.
 * For longer strings, transforms the string by keeping the first character as '1',
 * counting '1's in the middle (excluding first and last characters),
 * adding that many '1's, and ending with '0'.
 */
function processTestCases(testCases: {N: number, S: string}[]): string[] {
    const results: string[] = [];

    for (const {N, S} of testCases) {
        if (N <= 3) {
            results.push(S);
        } else {
            const middle = S.slice(1, -1);
            const countOnes = (middle.match(/1/g) || []).length;
            const ans = '1' + '1'.repeat(countOnes) + '0';
            results.push(ans);
        }
    }

    return results;
}

