// Define the TreeNode class to represent nodes in a binary search tree
class TreeNode {
    val: number;
    left: TreeNode | null;
    right: TreeNode | null;

    constructor(val: number = 0, left: TreeNode | null = null, right: TreeNode | null = null) {
        this.val = val;
        this.left = left;
        this.right = right;
    }
}

/**
 * Converts a binary search tree to a sorted doubly linked list in place.
 * Returns the head of the doubly linked list.
 */
function convertBstToDoublyLinkedList(root: TreeNode | null): TreeNode | null {
    if (!root) {
        return null;
    }

    let last: TreeNode | null = null;
    let head: TreeNode | null = null;

    // Helper function to perform the in-order traversal and link nodes
    function inOrderTraverse(node: TreeNode | null): void {
        if (!node) {
            return;
        }

        // Traverse left subtree
        inOrderTraverse(node.left);

        // Link the previous node (last) with the current one (node)
        if (last) {
            last.right = node;
            node.left = last;
        } else {
            head = node; // This will be the head of the doubly linked list
        }

        last = node; // Mark this node as the last processed node

        // Traverse right subtree
        inOrderTraverse(node.right);
    }

    inOrderTraverse(root);

    return head;
}

assertions:
const assert = require('assert');

function testConvertBstToDoublyLinkedList() {
    // Helper function to convert array to doubly linked list for comparison
    function createDoublyLinkedList(arr: number[]): TreeNode | null {
        if (arr.length === 0) return null;
        const head = new TreeNode(arr[0]);
        let current = head;
        for (let i = 1; i < arr.length; i++) {
            const newNode = new TreeNode(arr[i]);
            current.right = newNode;
            newNode.left = current;
            current = newNode;
        }
        return head;
    }

    // Helper function to compare two doubly linked lists
    function compareLists(l1: TreeNode | null, l2: TreeNode | null): boolean {
        while (l1 && l2) {
            if (l1.val !== l2.val) return false;
            l1 = l1.right;
            l2 = l2.right;
        }
        return l1 === null && l2 === null;
    }

    // Test case 1: Simple BST
    const testCase1 = new TreeNode(4,
        new TreeNode(2,
            new TreeNode(1),
            new TreeNode(3)
        ),
        new TreeNode(5)
    );
    const expected1 = createDoublyLinkedList([1, 2, 3, 4, 5]);
    assert(compareLists(convertBstToDoublyLinkedList(testCase1), expected1));

    // Test case 2: BST with only left children
    const testCase2 = new TreeNode(3,
        new TreeNode(2,
            new TreeNode(1),
            null
        ),
        null
    );
    const expected2 = createDoublyLinkedList([1, 2, 3]);
    assert(compareLists(convertBstToDoublyLinkedList(testCase2), expected2));

    // Test case 3: BST with only right children
    const testCase3 = new TreeNode(1,
        null,
        new TreeNode(2,
            null,
            new TreeNode(3)
        )
    );
    const expected3 = createDoublyLinkedList([1, 2, 3]);
