/**
 * Counts the number of "circles" in digits based on a predefined mapping.
 * The digit_circles array defines how many circles each digit (0-9) has.
 */
const digitCircles = [1, 0, 0, 0, 1, 0, 1, 0, 2, 1];
const maxVal = 10 ** 6;

/**
 * Precomputes the count of circles for all numbers from 0 to maxVal.
 * @returns An array where each index represents the count of circles for that number.
 */
function precomputeCountCircles(): number[] {
    const countCircles = new Array(maxVal + 1).fill(0);
    countCircles[0] = 1; // 0 has 1 circle

    for (let i = 1; i <= maxVal; i++) {
        let cnt = 0;
        let num = i;
        while (num > 0) {
            const digit = num % 10;
            cnt += digitCircles[digit];
            num = Math.floor(num / 10);
        }
        countCircles[i] = cnt;
    }

    return countCircles;
}

/**
 * Precomputes the prefix sums of the countCircles array.
 * @param countCircles The array containing circle counts for each number.
 * @returns An array of prefix sums.
 */
function precomputePrefixSums(countCircles: number[]): number[] {
    const prefixSum = new Array(maxVal + 1).fill(0);
    prefixSum[0] = countCircles[0];

    for (let i = 1; i <= maxVal; i++) {
        prefixSum[i] = prefixSum[i - 1] + countCircles[i];
    }

    return prefixSum;
}

/**
 * Calculates the total circles in numbers from a to b inclusive.
 * @param a The starting number (inclusive).
 * @param b The ending number (inclusive).
 * @param prefixSum The precomputed prefix sums array.
 * @returns The total number of circles in the range [a, b].
 */
function calculateRangeSum(a: number, b: number, prefixSum: number[]): number {
    if (a === 0) {
        return prefixSum[b];
    } else {
        return prefixSum[b] - (prefixSum[a - 1] ?? 0);
    }
}

// Precompute the necessary arrays
const countCircles = precomputeCountCircles();
const prefixSum = precomputePrefixSums(countCircles);

