/**
 * Returns an array with the sum, difference, product, and quotient 
 * of all the numbers in the list. 
 * If the list is empty, returns [0, 0, 0, 0].
 */
function calculateOperations(numbers: number[]): [number, number, number, number] {
    if (numbers.length === 0) {
        return [0, 0, 0, 0];
    }

    let totalSum = numbers.reduce((acc, num) => acc + num, 0);
    let totalDiff = numbers[0];
    let totalProduct = numbers[0];
    let totalQuotient = numbers[0];

    for (let i = 1; i < numbers.length; i++) {
        totalDiff -= numbers[i];
        totalProduct *= numbers[i];
        totalQuotient /= numbers[i];
    }

    return [totalSum, totalDiff, totalProduct, totalQuotient];
}

