/**
 * Computes the minimum total value based on the given array of numbers.
 * The algorithm involves calculating differences, prefix sums, and then
 * deriving the optimal delta1 to minimize the total squared differences.
 * 
 * @param x - The input array of numbers.
 * @returns The computed total value rounded to the nearest integer.
 */
function computeMinimumTotal(x: number[]): number {
    const n = x.length;

    if (n === 1) {
        return 0;
    }

    // Compute the c array and the prefix sums S
    const c: number[] = new Array(n).fill(0);  // c[1] to c[n-1]
    for (let i = 1; i < n; i++) {
        const prev = x[i - 1];
        const curr = x[i];
        c[i] = prev - curr + 1;
    }

    const S: number[] = new Array(n).fill(0);  // S[0] = 0, S[1] to S[n-1]
    for (let i = 1; i < n; i++) {
        S[i] = S[i - 1] + c[i];
    }

    const sumS = S.slice(1).reduce((acc, val) => acc + val, 0);

    const delta1 = -sumS / n;

    let total = delta1 ** 2;
    for (let i = 1; i < n; i++) {
        const delta_i = delta1 + S[i];
        total += delta_i ** 2;
    }

    return Math.round(total);
}

