/**
 * Checks if a matrix is altered by verifying if any row or column is not sorted in ascending order.
 * @param n - Number of rows in the matrix.
 * @param m - Number of columns in the matrix.
 * @param matrix - The matrix to check.
 * @returns "YES" if the matrix is altered, "NO" otherwise.
 */
function isMatrixAltered(n: number, m: number, matrix: number[][]): string {
    // Check rows
    for (const row of matrix) {
        for (let i = 1; i < row.length; i++) {
            if (row[i] < row[i - 1]) {
                return "YES";
            }
        }
    }

    // Check columns
    for (let col = 0; col < m; col++) {
        for (let row = 1; row < n; row++) {
            if (matrix[row][col] < matrix[row - 1][col]) {
                return "YES";
            }
        }
    }

    return "NO";
}

/**
 * Processes multiple test cases to determine if each matrix is altered.
 * @param T - Number of test cases.
 * @param testCases - Array of test cases, each containing n, m, and the matrix.
 * @returns Array of results ("YES" or "NO") for each test case.
 */
function robotCombatTournament(T: number, testCases: [number, number, number[][]][]): string[] {
    const results: string[] = [];
    for (const [n, m, matrix] of testCases) {
        const result = isMatrixAltered(n, m, matrix);
        results.push(result);
    }
    return results;
}

