        [2, 2, [[1, 3], [2, 4]]],                   // Altered (row 2 is not sorted)
        [2, 2, [[1, 2], [3, 1]]],                   // Altered (column 2 is not sorted)
        [1, 1, [[1]]],                              // Not altered (single element)
        [2, 3, [[1, 2, 3], [1, 2, 2]]]              // Not altered
    ];

    const results = robotCombatTournament(testCases.length, testCases);
    assert.strictEqual(results[0], "NO");
    assert.strictEqual(results[1], "NO");
    assert.strictEqual(results[2], "YES");
    assert.strictEqual(results[3], "NO");
    assert.strictEqual(results[4], "YES");
}

