/**
 * Calculates the minimum number of single-character insertions, deletions, or substitutions required
 * to transform string `a` into string `b` using space optimization techniques.
 * 
 * @param a - The first string.
 * @param b - The second string.
 * @returns The minimum edit distance between the two strings.
 */
function minEditDistance(a: string, b: string): number {
    const lenA = a.length;
    const lenB = b.length;

    // If one of the strings is empty
    if (lenA === 0) {
        return lenB;
    }
    if (lenB === 0) {
        return lenA;
    }

    // Ensure a is the smaller or equal sized string for space optimization
    let swapped = false;
    let strA = a;
    let strB = b;
    if (lenA > lenB) {
        [strA, strB] = [strB, strA];
        swapped = true;
    }

    const lenStrA = strA.length;
    const lenStrB = strB.length;

    let previousRow: number[] = Array.from({ length: lenStrA + 1 }, (_, i) => i);
    let currentRow: number[] = new Array(lenStrA + 1).fill(0);

    for (let i = 1; i <= lenStrB; i++) {
        currentRow[0] = i;
        for (let j = 1; j <= lenStrA; j++) {
            if (strB[i - 1] === strA[j - 1]) {
                currentRow[j] = previousRow[j - 1];
            } else {
                currentRow[j] = Math.min(
                    previousRow[j - 1],
                    previousRow[j],
                    currentRow[j - 1]
                ) + 1;
            }
        }
        [previousRow, currentRow] = [currentRow, previousRow];
    }

    return previousRow[lenStrA];
}

