/**
 * Computes the Stirling numbers of the first kind up to a given maximum number.
 * Stirling numbers are computed using the recurrence relation:
 * S(n, k) = S(n-1, k-1) + (n-1) * S(n-1, k)
 */
function computeStirlingNumbers(maxN: number): number[][] {
    const stirling: number[][] = Array.from({ length: maxN + 1 }, () => Array(maxN + 1).fill(0));
    stirling[0][0] = 1;

    for (let i = 1; i <= maxN; i++) {
        for (let j = 1; j <= i; j++) {
            stirling[i][j] = stirling[i-1][j-1] + (i-1) * stirling[i-1][j];
        }
    }

    return stirling;
}

/**
 * Computes the factorial numbers up to a given maximum number.
 */
function computeFactorials(maxN: number): number[] {
    const fact: number[] = Array(maxN + 1).fill(1);
    for (let i = 1; i <= maxN; i++) {
        fact[i] = fact[i-1] * i;
    }
    return fact;
}

/**
 * Computes the greatest common divisor (GCD) of two numbers using the Euclidean algorithm.
 */
function gcd(a: number, b: number): number {
    while (b !== 0) {
        const temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}

/**
 * Computes the sum of Stirling numbers for a given n and m, and returns the result as a simplified fraction.
 */
function computeFraction(n: number, m: number, stirling: number[][], fact: number[]): string {
    let sumStirling = 0;
    for (let k = 1; k <= m; k++) {
        sumStirling += stirling[n][k];
    }
    const denominator = fact[n];
    const commonDivisor = gcd(sumStirling, denominator);
    const numerator = sumStirling / commonDivisor;
    const simplifiedDenominator = denominator / commonDivisor;
    return `${numerator}/${simplifiedDenominator}`;
}
const assert = require('assert');

function testStirlingCalculations() {
    const maxN = 20;
    const stirlingNumbers = computeStirlingNumbers(maxN);
    const factorials = computeFactorials(maxN);

