/**
 * Returns the maximum perimeter of a triangle that can be formed with any three different elements from nums.
 * If no valid triangle can be formed, returns 0.
 * 
 * @param nums - An array of numbers representing the lengths of sticks.
 * @returns The maximum perimeter of a valid triangle or 0 if no valid triangle can be formed.
 */
function maximumPerimeterTriangle(nums: number[]): number {
    // Sort the array in descending order to easily access the largest elements
    const sortedNums = [...nums].sort((a, b) => b - a);

    // Iterate through the sorted array to find the first valid triangle
    for (let i = 0; i < sortedNums.length - 2; i++) {
        const a = sortedNums[i];
        const b = sortedNums[i + 1];
        const c = sortedNums[i + 2];

        // Check the triangle inequality theorem: a < b + c
        if (a < b + c) {
            return a + b + c;
        }
    }

    // Return 0 if no valid triangle is found
    return 0;
}

