    assert.strictEqual(maximumPerimeterTriangle([1, 1, 1, 3, 3]), 7);
    assert.strictEqual(maximumPerimeterTriangle([1, 2, 3, 4, 5]), 12);
    assert.strictEqual(maximumPerimeterTriangle([1, 2]), 0);
}

