/**
 * Calculates the minimum edit distance between two strings using dynamic programming.
 * The edit distance is the minimum number of operations (insert, delete, substitute) 
 * required to transform string S into string T.
 * 
 * @param S - The source string.
 * @param T - The target string.
 * @returns The minimum edit distance between S and T.
 */
function computeEditDistance(S: string, T: string): number {
    const lenS = S.length;
    const lenT = T.length;

    // Initialize a DP table with (lenS + 1) rows and (lenT + 1) columns
    const dp: number[][] = Array.from({ length: lenS + 1 }, () => new Array(lenT + 1).fill(0));

    // Base cases
    for (let i = 0; i <= lenS; i++) {
        dp[i][0] = i; // Deleting i characters from S to match empty T
    }
    for (let j = 0; j <= lenT; j++) {
        dp[0][j] = j; // Inserting j characters into S to match T's first j characters
    }

    for (let i = 1; i <= lenS; i++) {
        for (let j = 1; j <= lenT; j++) {
            if (S[i - 1] === T[j - 1]) {
                dp[i][j] = dp[i - 1][j - 1];
            } else {
                dp[i][j] = Math.min(
                    dp[i - 1][j] + 1,     // delete
                    dp[i][j - 1] + 1,     // insert
                    dp[i - 1][j - 1] + 1  // substitute
                );
            }
        }
    }

    return dp[lenS][lenT];
}

