/**
 * Calculates the sum of a subarray multiplied by the count of distinct elements in the subarray.
 * @param n - The length of the array (unused in the function, but kept for consistency with the original function signature).
 * @param q - The number of queries (unused in the function, but kept for consistency with the original function signature).
 * @param array - The input array of numbers.
 * @param queries - An array of tuples, where each tuple contains the left and right indices (1-based) of the subarray.
 * @returns An array of results, where each result is the sum of the subarray multiplied by the count of distinct elements in the subarray.
 */
function querySubarraySums(n: number, q: number, array: number[], queries: [number, number][]): number[] {
    const results: number[] = [];
    for (const [l, r] of queries) {
        const subarray = array.slice(l - 1, r);
        const subarraySum = subarray.reduce((sum, num) => sum + num, 0);
        const distinctCount = new Set(subarray).size;
        results.push(subarraySum * distinctCount);
    }
    return results;
}

