/**
 * Calculates the fewest operations required to reduce a number to 1 using the following operations:
 * - Subtract 1
 * - Divide by 2 (if divisible by 2)
 * - Divide by 3 (if divisible by 3)
 * 
 * Uses dynamic programming to build up the solution from smaller subproblems.
 */
function fewestOperations(n: number): number {
    if (n === 1) {
        return 0;
    }

    const dp: number[] = new Array(n + 1).fill(0);
    for (let i = 2; i <= n; i++) {
        dp[i] = dp[i - 1] + 1;
        if (i % 2 === 0) {
            dp[i] = Math.min(dp[i], dp[i / 2] + 1);
        }
        if (i % 3 === 0) {
            dp[i] = Math.min(dp[i], dp[i / 3] + 1);
        }
    }

    return dp[n];
}

