/**
 * Updates the state of a parking garage based on a series of add/remove events.
 * 
 * @param L Number of levels.
 * @param N Number of rows per level.
 * @param M Number of columns per level.
 * @param initialState The initial state of the parking garage as a 3D array of strings.
 * @param events List of events to process, each event is a string with operation, level, row, and column.
 * @returns The final state of the parking garage after all events have been processed.
 */
function updateParkingGarage(
    L: number,
    N: number,
    M: number,
    initialState: string[][][],
    events: string[]
): string[][][] {
    // Create a deep copy of the initial state to avoid modifying the original array
    const state = initialState.map(level => level.map(row => [...row]));

    for (const event of events) {
        const [operation, levelStr, rowStr, colStr] = event.split(' ');
        const level = parseInt(levelStr, 10);
        const row = parseInt(rowStr, 10);
        const col = parseInt(colStr, 10);

        if (operation === "ADD") {
            state[level][row][col] = 'C';
        } else if (operation === "REMOVE") {
            state[level][row][col] = '.';
        }
    }

    return state;
}

/**
 * Formats the parking garage state into a string representation.
 * 
 * @param parkingGarage The parking garage state as a 3D array of strings.
 * @returns A string representation of the parking garage.
 */
function formatParkingGarage(parkingGarage: string[][][]): string {
    return parkingGarage.map(level => 
        level.map(row => row.join('')).join('\n')
    ).join('\n');
}

