            [9, 10, 11, 12],
            [13, 14, 15, 16]
        ]),
        [[6, 7], [10, 11]]
    );

    // Test 3x3 matrix
    assert.deepStrictEqual(
        extractMatrixCenter([
            [1, 2, 3],
            [4, 5, 6],
            [7, 8, 9]
        ]),
        [[5]]
    );

