/**
 * Extracts the center of a matrix by removing the first and last rows and columns.
 * If the matrix has 2 or fewer rows or columns, returns an empty array.
 * 
 * @param matrix - A 2D array of numbers representing the matrix.
 * @returns A 2D array representing the center of the matrix.
 */
function extractMatrixCenter(matrix: number[][]): number[][] {
    const rows = matrix.length;
    if (rows <= 2) {
        return [];
    }

    const cols = matrix[0].length;
    if (cols <= 2) {
        return [];
    }

    const centerMatrix: number[][] = [];
    for (let i = 1; i < rows - 1; i++) {
        const centerRow = matrix[i].slice(1, cols - 1);
        centerMatrix.push(centerRow);
    }

    return centerMatrix;
}
