/**
 * Determines if an array can be partitioned into two parts where the sum of the left part
 * is greater than the sum of the right part at any point during iteration.
 * @param testCases An array of tuples where each tuple contains a number `n` (length of the array) 
 * and an array `a` of numbers.
 * @returns An array of strings ("Yes" or "No") indicating whether each test case can be partitioned.
 */
function canPartitionArray(testCases: [number, number[]][]): string[] {
    const results: string[] = [];

    for (const [n, a] of testCases) {
        const totalSum = a.reduce((sum, num) => sum + num, 0);
        let leftSum = 0;
        let canPartition = false;

        for (let i = 0; i < n - 1; i++) {
            leftSum += a[i];
            const rightSum = totalSum - leftSum;

            if (leftSum > rightSum) {
                results.push("Yes");
                canPartition = true;
                break;
            }
        }

        if (!canPartition) {
            results.push("No");
        }
    }

    return results;
}

