/**
 * Finds the shortest path in a grid from top-left to bottom-right.
 * Grid cells with '.' are passable, other cells are blocked.
 * Uses BFS to find the shortest path.
 * Returns the path length or "Impossible" if no path exists.
 */
function shortestPath(n: number, m: number, grid: string[]): number | string {
    const directions = [[0, 1], [1, 0], [0, -1], [-1, 0]];

    function isValid(x: number, y: number): boolean {
        return x >= 0 && x < n && y >= 0 && y < m && grid[x][y] === '.';
    }

    const visited: boolean[][] = Array.from({ length: n }, () => new Array(m).fill(false));
    const queue: [number, number, number][] = [[0, 0, 0]]; // [x, y, distance]
    visited[0][0] = true;

    while (queue.length > 0) {
        const [x, y, dist] = queue.shift()!;

        if (x === n - 1 && y === m - 1) {
            return dist;
        }

        for (const [dx, dy] of directions) {
            const nx = x + dx;
            const ny = y + dy;
            if (isValid(nx, ny) && !visited[nx][ny]) {
                visited[nx][ny] = true;
                queue.push([nx, ny, dist + 1]);
            }
        }
    }

    return "Impossible";
}

assertions:
