function testShortestPath() {
    // Test case 1: 3x3 open grid
    assert.strictEqual(
        shortestPath(3, 3, ["...", "...", "..."]),
        4
    );

    // Test case 2: 3x3 grid with one obstacle
    assert.strictEqual(
        shortestPath(3, 3, ["...", ".#.", "..."]),
        4
    );

    // Test case 3: 2x2 grid with blocked exit
    assert.strictEqual(
        shortestPath(2, 2, ["..", "#."]),
        2
    );

    // Test case 4: 5x5 grid with alternating blocked rows
    assert.strictEqual(
        shortestPath(5, 5, [".....", "#####", ".....", "#####", "....."]),
        "Impossible"
    );
}

testShortestPath();

