/**
 * Calculates a special value based on a number N and base P.
 * The formula is: (N + 1) - product of (digit + 1) for all digits of N in base P.
 * @param N The original number
 * @param P The base to use for digit decomposition
 * @returns The calculated special value
 */
function calculateSpecialValue(N: number, P: number): number {
    let original = N;
    let product = 1;
    let n = N;

    while (n > 0) {
        const rem = n % P;
        product *= (rem + 1);
        n = Math.floor(n / P);
    }

    return (original + 1) - product;
}

